import React, { useState } from "react"

const CreateNote =()=>{

    const[name,setName]=useState({
        title:"",
        content:"",
    });

    const added =(event)=>{
        const value= event.target.value;
        const name= event.target.name;

        setName((preValue)=>{
            return({...preValue,name:[value]})
        })
    }
    const clicked =(event)=>{
        Props.passNote(name);
        setName({
            title:"",
            content:"",
        })
    }

    return (
        <div className="main_note">
            <input type="text" placeholder="Title" value={name.title} name="title"  onChange={added}></input>
            <textarea rows="" column="" placeholder="Add a Note" value={name.content} name="Content" onChange={added}></textarea>
            <button className="btn" onClick={clicked}> Add</button>
        </div>
    )
}

export default CreateNote;
